import React from "react";
import item1 from "../img/item-1.png";
import item4 from "../img/item-4.png";
import Data from "../data/store-items.json";
import { matchPath } from "react-router";
class Shirt extends React.Component {
  state = {
    items: Data,
  };
  render() {
    const itemId = this.props.path;
    console.log(itemId);
    const items = this.state.items.find((item) => item._id.includes(itemId));
    return (
      <section className="shirt">
        <div className="container">
          <ul>
            <li>
              <img src={item1}></img>
            </li>

            <li>
              <img src={item1}></img>
            </li>
            <li>
              <img src={item1}></img>
            </li>
          </ul>

          <div>
            <img className="bigShirt" src={Data.image} width="400px"></img>
          </div>

          <div className="Shirt__desc">
            <h1>{items.price}</h1>
            <h2>{this.props.name}</h2>
            <p>
              Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
              eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut
              enim ad minim veniam, quis nostrud exercitation ullamco laboris
              nisi ut aliquip ex ea commodo consequat. Duis aute irure
            </p>
            <div className="options">
              <ul>
                <li>
                  <b>color:</b> pink
                </li>
                <li>
                  <div className="pink"></div>
                  <div className="blue"></div>
                  <div className="black"></div>
                </li>
              </ul>
            </div>
            <div className="buying">
              <input type="number"></input>
              <button>ADD TO CART</button>
            </div>
          </div>
        </div>
      </section>
    );
  }
}
export default Shirt;
