  return (
    <div className="item">
      <div className="item__image">
        <NavLink exact to="/item">
          <img src={require(`../img/${props.pic}`)} />
        </NavLink>
      </div>
      <div className="item__desc">
        {props.title}
        <button className="add-btn">ADD TO CART</button>
        <h5>{props.price}</h5>
      </div>
    </div>
  );
};

export default TheItem;
