import React from "react";
import item1 from "../img/item-1.png";

import Data from "../data/store-items.json";
import { Link } from "react-router-dom";

class Shirt extends React.Component {
  state = {
    items: Data,
  };
  render() {
    cartAdding = async (selectedItem) => {
      try {
        const MyItem = {
          name: selectedItem.name,
          price: selectedItem.price,
          description: selectedItem.description,
        };
        await this.setState({ cartItems: [...this.state.cartItems, MyItem] });
        alert("keep going ");
      } catch (error) {
        alert("no");
      }
    };

    const itemId = this.props.path;
    const pro = this.state.items.filter((item) => item._id === itemId);

    return (
      <section className="shirt">
        <div className="container">
          <ul>
            <li>
              <img src={item1}></img>
            </li>

            <li>
              <img src={item1}></img>
            </li>
            <li>
              <img src={item1}></img>
            </li>
          </ul>

          <div>
            <img
              className="bigShirt"
              src={require(`../img/${pro[0].image}`)}
              width="400px"
            ></img>
          </div>

          <div className="Shirt__desc">
            <h1>{pro[0].price}</h1>
            <h2>{pro[0].name}</h2>
            <p>
              Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
              eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut
              enim ad minim veniam, quis nostrud exercitation ullamco laboris
              nisi ut aliquip ex ea commodo consequat. Duis aute irure
            </p>
            <div className="options">
              <ul>
                <li>
                  <b>color:</b> pink
                </li>
                <li>
                  <div className="pink"></div>
                  <div className="blue"></div>
                  <div className="black"></div>
                </li>
              </ul>
