import React from "react";
import HomePage from "./pages/HomePage";
import OneItem from "./pages/OneItem";
import { BrowserRouter, Route } from "react-router-dom";
import Header from "./component/Header";
import Footer from "./component/Footer";
import Cart from "./pages/cart";
import AddCart from "./component/AddCart";

class App extends React.Component {
  state = {
    cartItems: [],
  };
  cartAdding = async (selectedItem) => {
    console.log(selectedItem, "selec");
    try {
      const MyItem = {
        name: selectedItem.name,
        price: selectedItem.price,
        description: selectedItem.description,
      };

      await this.setState((state) => {
        const list = state.cartItems.concat(selectedItem);
        console.log(list, "list");
        console.log(state.cartItems, "state");
        return {
          cartItems: list,
        };
      });
      alert("item is added to cart ");
    } catch (error) {
      alert("error");
    }
  };
  render() {
    return (
      <BrowserRouter>
        <Header />
        <>
          <Route exact path="/" component={HomePage} />
          <Route
            exact
            path="/item/:_id"
            render={(props) => (
              <OneItem {...props} onAdding={this.cartAdding} />
            )}
          />

          <Route
            exact
            path="/cart"
            render={(props) => (
              <Cart {...props} cartData={this.state.cartItems} />
            )}
          />
        </>
        <Footer />
      </BrowserRouter>
    );
  }
}

export default App;
