import React, { Component } from "react";
import Data from "../data/store-items.json";
class Cart extends React.Component {
  state = {
    items: Data,
  };
  render() {
    const itemId = this.props.path;
    const cart = Data.filter((item) => item._id === itemId);
    console.log(cart);
    console.log(this.props.cartData, "cart");
    return (
      <>
        <div className="container">
          <div className="item__row">
            <div className="image__row">
              <img src={this.props.cartData.image}></img>
            </div>
            <div className="desc__row">
              <h1>{this.props.name}</h1>
            </div>
          </div>
        </div>
      </>
    );
  }
}

export default Cart;
