import React from "react";
import { NavLink } from "react-router-dom";

class TheItem extends React.Component {
  render() {
    return (
      <div className="item">
        <div className="item__image">
          <NavLink exact to="/item">
            <img src={this.props.pic} alt="item"></img>
          </NavLink>
        </div>
        <div className="item__desc">
          {this.props.title}
          <h5>{this.props.price}</h5>
        </div>
      </div>
    );
  }
}

export default TheItem;
