import React from "react";
import { NavLink, Link } from "react-router-dom";
import data from "../data/store-items.json";
const TheItem = (props) => {
  state = {
    items: data,
  };
  return (
    <div className="item">
      <div className="item__image">
        <Link
          to={{
            pathname: `/item${props.item._id}`,
            aboutProps: {
              name: props.title,
              price: props.price,
              image: require(`../img/${props.pic}`),
            },
          }}
        >
          <img src={require(`../img/${props.pic}`)} />
        </Link>
      </div>
      <div className="item__desc">
        {props.title}
        <h5>{props.price}</h5>
      </div>
    </div>
  );
  console.log("testing", props.location.aboutProps);
};

export default TheItem;
