import React, { Component } from "react";
import Data from "../data/store-items.json";
const Cart = (props) => {
  state = {
    items: Data,
  };
  const itemId = props.path;
  const cart = Data.filter((item) => item._id === itemId);
  console.log(cart);
  return <></>;
};

export default Cart;
