import React from "react";
import HomePage from "./pages/HomePage";
import OneItem from "./pages/OneItem";
import { BrowserRouter, Route } from "react-router-dom";
import Header from "./component/Header";
import Footer from "./component/Footer";
import Cart from "./pages/cart";
import AddCart from "./component/AddCart";

class App extends React.Component {
  state = {
    cartItems: [],
  };
  cartAdding = (selectedItem) => {
    try {
      const MyItem = {
        name: selectedItem.name,
        price: selectedItem.price,
        description: selectedItem.description,
      };
      this.setState({ cartItems: [...this.state.cartItems, MyItem] });
      alert("keep going ");
    } catch (error) {
      alert("no");
    }
  };
  render() {
    return (
      <BrowserRouter>
        <Header />
        <>
          <Route exact path="/" component={HomePage} />
          <Route
            exact
            path="/item/:_id"
            component={OneItem}
            onAdding={this.cartAdding()}
          />
          <Route exact path="/cart/:_id" component={AddCart} />
          <Route exact path="/item/item/:_id" component={OneItem} />
          <Route
            exact
            path="/cart"
            component={Cart}
            cartData={this.state.cartItems}
          />
        </>
        <Footer />
      </BrowserRouter>
    );
  }
}

export default App;
