import React from "react";
import { NavLink, Link } from "react-router-dom";

const TheItem = (props) => {
  console.log("testing", props.location.aboutProps);
  return (
    <div className="item">
      <div className="item__image">
        <Link
          to={{
            pathname: "/item",
            aboutProps: {
              name: this.props.title,
              price: this.props.price,
              image: require(`../img/${this.props.pic}`),
            },
          }}
        >
          <img src={require(`../img/${this.props.pic}`)} />
        </Link>
      </div>
      <div className="item__desc">
        {this.props.title}
        <h5>{this.props.price}</h5>
      </div>
    </div>
  );
};

export default TheItem;
